if [[ "$needTest" == "yes" ]]
then
	echo "testing route BBH/R497A: BBH/S497->SHM/S515..."

	# cancelling conflict routes and the route itself: 

	# pick up tracks on conflicting routes (including itself) [BBH/R497A: BBH/S497->SHM/S515]
	pickUpCount=0
	pickUpTrackIfOccupied TCMS BBH/T477 http://westracesim.TCMS:8080/BBH/mnem/477.TP/value
	pickUpTrackIfOccupied TCMS BBH/T497 http://westracesim.TCMS:8080/BBH/mnem/497.TP/value
	pickUpTrackIfOccupied TCMS BBH/T499 http://westracesim.TCMS:8080/BBH/mnem/499.TP/value
	pickUpTrackIfOccupied TCMS SHM/T515 http://westracesim.TCMS:8080/SHM/mnem/515.TP/value

	domain="TCMS"
	waitTracksPickUp BBH/T477 BBH/T497 BBH/T499 SHM/T515

	cancelSignalIfRouteSet TCMS BBH/S497

	# un-block all tracks in the route...
	unBlockTrackCircuit TCMS BBH/T499
	unBlockTrackCircuit TCMS BBH/T497

	domain="TCMS"
	waitTracksUnBlock BBH/T499 BBH/T497


	#setting the route ...
	setRoute TCMS BBH/R497A BBH/S497 SHM/S515
	sleep 3

	if [[ "$routeSet" == "yes" || "$routeStored" == "yes" ]]
	then

		#cancelling the route ...
		cancelSignal TCMS BBH/S497
	fi

fi


